import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FiHome, FiDollarSign, FiUsers, FiCheck, FiPlus } from 'react-icons/fi';
import { format } from 'date-fns';

const Dashboard = () => {
  const [stats, setStats] = useState({
    activeListings: 0,
    activeListingsChange: 0,
    commission: 0,
    commissionChange: 0,
    newLeads: 0,
    newLeadsChange: 0
  });
  const [activities, setActivities] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch data
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [propertiesRes, usersRes] = await Promise.all([
          axios.get('/api/properties'),
          axios.get('/api/users')
        ]);

        const properties = propertiesRes.data;
        const users = usersRes.data;

        // Calculate stats
        const activeListings = properties.filter(p => p.status === 'active').length;
        const soldListings = properties.filter(p => p.status === 'sold');
        const commission = soldListings.reduce((acc, curr) => acc + (curr.price * 0.03), 0);

        const sevenDaysAgo = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000);
        const newLeads = users.filter(u => u.role === 'lead' && new Date(u.created_at) > sevenDaysAgo).length;

        setStats({
          activeListings,
          commission,
          newLeads,
          // Mocking change values for now
          activeListingsChange: 3,
          commissionChange: 12800,
          newLeadsChange: 8
        });

        setLoading(false);
      } catch (error) {
        console.error('Error fetching dashboard data:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Format relative time
  const formatRelativeTime = (timestamp) => {
    const now = new Date();
    const diffInMinutes = Math.floor((now - timestamp) / (1000 * 60));

    if (diffInMinutes < 60) {
      return `${diffInMinutes} minute${diffInMinutes !== 1 ? 's' : ''} ago`;
    } else if (diffInMinutes < 24 * 60) {
      const hours = Math.floor(diffInMinutes / 60);
      return `${hours} hour${hours !== 1 ? 's' : ''} ago`;
    } else {
      const days = Math.floor(diffInMinutes / (60 * 24));
      return days === 1 ? 'Yesterday' : `${days} days ago`;
    }
  };

  // Activity icon based on type
  const getActivityIcon = (type) => {
    switch (type) {
      case 'showing':
        return <div className="bg-blue-500 rounded-full p-2"><FiHome className="h-4 w-4 text-white" /></div>;
      case 'offer':
        return <div className="bg-green-500 rounded-full p-2"><FiDollarSign className="h-4 w-4 text-white" /></div>;
      case 'lead':
        return <div className="bg-purple-500 rounded-full p-2"><FiUsers className="h-4 w-4 text-white" /></div>;
      case 'listing':
        return <div className="bg-red-500 rounded-full p-2"><FiHome className="h-4 w-4 text-white" /></div>;
      default:
        return <div className="bg-gray-500 rounded-full p-2"><FiHome className="h-4 w-4 text-white" /></div>;
    }
  };

  // Toggle task completion
  const toggleTaskStatus = async (id) => {
    try {
      // Will implement real API call here
      setTasks(tasks.map(task => 
        task.id === id ? { ...task, completed: !task.completed } : task
      ));
    } catch (error) {
      console.error('Error toggling task:', error);
    }
  };

  if (loading) {
    return <div className="flex justify-center items-center h-full">Loading...</div>;
  }

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
      {/* Stats Cards */}
      <div className="bg-white rounded-lg shadow p-6">
        <div className="flex items-center">
          <div className="p-3 rounded-full bg-blue-100 text-blue-600">
            <FiHome className="h-6 w-6" />
          </div>
          <div className="ml-4">
            <h2 className="text-sm font-semibold text-gray-700">Active Listings</h2>
            <p className="text-2xl font-bold text-gray-900">{stats.activeListings}</p>
          </div>
        </div>
        <div className="mt-4">
          <div className="flex items-center justify-between">
            <span className="text-sm text-gray-500">Last 30 days</span>
            <span className="text-sm text-green-600 font-semibold">
              +{stats.activeListingsChange} ({Math.round((stats.activeListingsChange / stats.activeListings) * 100)}%)
            </span>
          </div>
          <div className="w-full h-2 bg-gray-200 rounded-full mt-2">
            <div 
              className="h-full bg-green-500 rounded-full" 
              style={{ width: `${Math.min(100, Math.round((stats.activeListingsChange / stats.activeListings) * 100))}%` }}
            ></div>
          </div>
        </div>
      </div>

      <div className="bg-white rounded-lg shadow p-6">
        <div className="flex items-center">
          <div className="p-3 rounded-full bg-green-100 text-green-600">
            <FiDollarSign className="h-6 w-6" />
          </div>
          <div className="ml-4">
            <h2 className="text-sm font-semibold text-gray-700">Commission</h2>
            <p className="text-2xl font-bold text-gray-900">${stats.commission.toLocaleString()}</p>
          </div>
        </div>
        <div className="mt-4">
          <div className="flex items-center justify-between">
            <span className="text-sm text-gray-500">YTD</span>
            <span className="text-sm text-green-600 font-semibold">
              +${stats.commissionChange.toLocaleString()}
            </span>
          </div>
          <div className="w-full h-2 bg-gray-200 rounded-full mt-2">
            <div 
              className="h-full bg-green-500 rounded-full" 
              style={{ width: `${Math.min(100, Math.round((stats.commissionChange / stats.commission) * 100))}%` }}
            ></div>
          </div>
        </div>
      </div>

      <div className="bg-white rounded-lg shadow p-6">
        <div className="flex items-center">
          <div className="p-3 rounded-full bg-purple-100 text-purple-600">
            <FiUsers className="h-6 w-6" />
          </div>
          <div className="ml-4">
            <h2 className="text-sm font-semibold text-gray-700">New Leads</h2>
            <p className="text-2xl font-bold text-gray-900">{stats.newLeads}</p>
          </div>
        </div>
        <div className="mt-4">
          <div className="flex items-center justify-between">
            <span className="text-sm text-gray-500">Last 7 days</span>
            <span className="text-sm text-green-600 font-semibold">
              +{stats.newLeadsChange} ({Math.round((stats.newLeadsChange / stats.newLeads) * 100)}%)
            </span>
          </div>
          <div className="w-full h-2 bg-gray-200 rounded-full mt-2">
            <div 
              className="h-full bg-green-500 rounded-full" 
              style={{ width: `${Math.min(100, Math.round((stats.newLeadsChange / stats.newLeads) * 100))}%` }}
            ></div>
          </div>
        </div>
      </div>

      {/* Recent Activities */}
      <div className="md:col-span-2 bg-white rounded-lg shadow p-6">
        <h2 className="text-lg font-semibold text-gray-800 mb-4">Recent Activities</h2>
        <div className="space-y-4">
          {activities.map(activity => (
            <div key={activity.id} className="flex items-start">
              <div className="flex-shrink-0">
                {getActivityIcon(activity.type)}
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-900">{activity.title}</p>
                <p className="text-sm text-gray-500">{activity.description}</p>
                <p className="text-xs text-gray-400 mt-1">{formatRelativeTime(activity.timestamp)}</p>
              </div>
            </div>
          ))}
        </div>
        <button className="mt-4 text-sm text-blue-600 hover:text-blue-800">
          View all activities
        </button>
      </div>

      {/* Upcoming Tasks */}
      <div className="bg-white rounded-lg shadow p-6">
        <h2 className="text-lg font-semibold text-gray-800 mb-4">Upcoming Tasks</h2>
        {tasks.length > 0 ? (
          <ul className="space-y-3">
            {tasks.filter(task => !task.completed).slice(0, 3).map(task => (
              <li key={task.id} className="flex items-start">
                <input 
                  type="checkbox" 
                  className="mt-1 h-4 w-4 text-blue-600 rounded border-gray-300 focus:ring-blue-500"
                  onChange={() => toggleTaskStatus(task.id)} 
                  checked={task.completed}
                />
                <div className="ml-3">
                  <p className="text-sm font-medium text-gray-900">{task.title}</p>
                  <p className="text-xs text-gray-500">Due: {format(new Date(task.dueDate), 'MMM dd, yyyy')}</p>
                </div>
              </li>
            ))}
          </ul>
        ) : (
          <div className="flex flex-col items-center justify-center h-32 text-gray-400">
            <p className="mb-2">No upcoming tasks</p>
            <button className="flex items-center text-blue-600 hover:text-blue-800">
              <FiPlus className="mr-1" /> Add a task
            </button>
          </div>
        )}
        <button 
          className="mt-4 w-full py-2 px-4 bg-blue-600 text-white rounded hover:bg-blue-700 flex items-center justify-center"
          onClick={() => window.location.href = '/tasks'}
        >
          <FiPlus className="mr-2" /> Add New Task
        </button>
      </div>
    </div>
  );
};

export default Dashboard;
